//  src/http/index

import myaxios from './MyAxios';
const httpApi = {
	/**
	 * 查询所有演员列表
	 * @returns Promise
	 */
	queryAllActor() {
		let url = 'http://localhost:3010/movie-actors';
		let param = { page: 1, pagesize: 100 };
		return myaxios.get(url, param);
	},
	/**
	 * 通过演员关键字查找
	 * @param {Object} param 查询演员的参数 { name:关键字 }
	 * @returns Promise
	 */
	queryActorByName(param) {
		let url = 'http://localhost:3010/movie-actors/name';
		return myaxios.post(url, param);
	},
};

export default httpApi;
