//  src/http/index
// 整合所有子模块的入口

import actorApi from './actorApi/actorApi.js';
const httpApi = {
	/**
	 * 查询所有演员列表
	 * @returns Promise
	 */
	queryAllActor() {
		let url = 'http://localhost:3010/movie-actors';
		let param = { page: 1, pagesize: 100 };
		return myaxios.get(url, param);
	},
	/**
	 * 通过演员关键字查找
	 * @param {Object} param 查询演员的参数 { name:关键字 }
	 * @returns Promise
	 */
	queryActorByName(param) {
		let url = 'http://localhost:3010/movie-actors/name';
		return myaxios.post(url, param);
	},
	/**
	 * 通过id删除演员
	 * @param {Object} param  { id:演员id }
	 * @returns Promise
	 */
	DeleteActorById(param) {
		let url = 'http://localhost:3010/movie-actor/del';
		return myaxios.post(url, param);
	},
};

export default httpApi;
